ee:variables.ee:set-variable:
%dw 2.0
output application/java
---
now() as String {
	format : "yyyy-MM-dd'T'HH:mm:ss.SSS'Z'"
}

%dw 2.0
output application/json

var filters = {
    "siteCodeFilter": if (!isEmpty(vars.siteCode)) "code eq '$(vars.siteCode)' " else "",
    "sarfCodeFilter": if (!isEmpty(vars.sarfCode)) "code eq '$(vars.sarfCode)' " else ""
}
---
{
	filter: (valuesOf(filters) filter !isBlank($)) joinBy " or "
}
